rc = channel_request_exec(channel, "ps aux");
if (rc &gt; 0) {
 return -1;
}
while ((rc = channel_read(channel, buffer, sizeof(buffer), 0)) &gt; 0) {
 if (fwrite(buffer, 1, rc, stdout) != (unsigned int) rc) {
 return -1;
 }
}

