bool al_init_font_addon(void);
bool al_init_ttf_addon(void);

* Type: ALLEGRO_COLOR
 */
typedef struct ALLEGRO_COLOR ALLEGRO_COLOR;

struct ALLEGRO_COLOR
{
 float r, g, b, a;
};
